mapper.insert:
insert into xhUserAddress
addID, userID, addName, addUserName, addCountry, addProvice, addCity, addDistrict, addAddress, addCode, addPhone,
#{addID }, #{userID }, #{addName }, #{addUserName }, #{addCountry }, #{addProvice }, #{addCity }, #{addDistrict }, #{addAddress }, #{addCode }, #{addPhone }

mapper.select:
select  from xhUserAddress
where userID = #{userid }
order by isDefault desc

mapper.update:
update xhUserAddress set
	addID = #{addID }, 
	addUserName = #{addUserName }, 
	addProvice = #{addProvice }, addCity = #{addCity }, addDistrict = #{addDistrict }, 
	addAddress = #{addAddress }, addCode = #{addCode }, addPhone = #{addPhone }
where addID = #{addID }

update xhUserAddress set
	isDefault = 1
where addID = #{addID } and userID = #{userID }

update xhUserAddress set
	isDefault = 0
where userID = #{userID }
